CREATE DATABASE gameMais;
USE gameMais;

CREATE TABLE Jogos (
    id_jogo INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(100) NOT NULL,
    categoria VARCHAR(100) NOT NULL,
    valor_R$ DOUBLE(10,2) NOT NULL,
    quantidade INT NOT NULL,
    ano_lancamento YEAR NOT NULL,
    empresa VARCHAR(100) NOT NULL,
    plataforma VARCHAR(100) NOT NULL,
    quantidade_vendida INT NOT NULL,
    avaliacao ENUM('1.0', '1.5', '2.0', '2.5', '3.0', '3.5', '4.0', '4.5', '5.0') NOT NULL,
    data_disponibilidade DATE NOT NULL,
    faixa_etaria VARCHAR(50) NOT NULL,
    mes_mais_vendido ENUM('Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro') NOT NULL,
    mes_menos_vendido ENUM('Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro') NOT NULL,
    versao VARCHAR(100) NOT NULL,
    garantia DATE,
    idiomas VARCHAR(100) NOT NULL,
    modo_de_jogo VARCHAR(100) NOT NULL,
    desconto DOUBLE(10,2),
    tempo_medio_do_jogo_H TIME NOT NULL,
    suporte_expansoes ENUM('Sim', 'Não') NOT NULL
);







